import React, { useState, useEffect } from "react";
import { getTokenBalance, mintNFT } from "./utils/contract";

function App() {
  const [account, setAccount] = useState<string | null>(null);
  const [balance, setBalance] = useState<string | null>(null);
  const [symbol, setSymbol] = useState<string>("");

  const connectWallet = async () => {
    if (!window.ethereum) return alert("Installez MetaMask !");
    const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
    const selected = accounts[0];
    setAccount(selected);

    const tokenData = await getTokenBalance(selected);
    if (tokenData) {
      setBalance(tokenData.balance);
      setSymbol(tokenData.symbol);
    }
  };

  const handleMintNFT = async () => {
    if (!account) return;
    try {
      const tx = await mintNFT(account, "https://example.com/nft-metadata.json");
      alert("NFT mintÃ© avec succÃ¨s ! ðŸš€ Tx Hash : " + tx.hash);

      await fetch("http://ns3076776.ip-217-182-194.eu:4000/api/logs", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          address: account,
          action: "minted_nft",
          metadata: {
            txHash: tx.hash,
            tokenURI: "https://example.com/nft-metadata.json"
          }
        })
      });
    } catch (err) {
      console.error("Erreur minting NFT :", err);
      alert("Erreur lors du mint du NFT");
    }
  };

  useEffect(() => {
    connectWallet();
  }, []);

  return (
    <div style={{ padding: "2rem", fontFamily: "sans-serif" }}>
      <h1>Bienvenue sur EST FanVerse ðŸ¦…</h1>
      <p>Adresse : {account}</p>
      <p>Solde : {balance} {symbol}</p>

      <button onClick={handleMintNFT} style={{ padding: "1rem", marginTop: "1rem", backgroundColor: "#e60000", color: "white", border: "none", borderRadius: "5px" }}>
        ðŸŽ¨ Minter un NFT
      </button>
    </div>
  );
}

export default App;
